import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import axios from "axios"

// 1. call API on Load: axios, UseEffect
// 2. variable change: useState
// 3. Links: Link

const Dashboard = () => {
    const [shopList, SetShopList] = useState([])

    useEffect(() => {
        axios.get(`http://localhost:8000/api/shops`)
            .then(res => {
                SetShopList(res.data)
            })
            .catch(error => console.log(error))
    }, [])

    const handleDelete = (deleteId=>{
        axios.delete(`http://localhost:8000/api/shops/${deleteId}`)
            .then(res=>{
                const filteredList = shopList.filter((eachShop)=>eachShop._id !== deleteId)
                SetShopList(filteredList)
            })
            .catch(error=>console.log(error))
    })

    return (
        <div>
            <h1>Find stores in your area!</h1>

            <table className='table table-striped border-dark bordered-table'>
                <thead>
                    <tr>
                        <th>Store</th>
                        <th>Store Number</th>
                        <th>Open</th>
                        <th>Remove</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        shopList.map((eachShop, idx) => (
                            <tr  key={idx}>
                                <td> <Link to={`/shops/${eachShop._id}`}>{eachShop.store} </Link></td>
                                <td>{eachShop.number}</td>
                                <td>{eachShop.isOpen ? "True" : "False"}</td>
                                <td><button className='btn btn-outline-danger text-black' onClick={() => handleDelete(eachShop._id)}>Delete</button></td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>
            <p><Link className='btn btn-outline-primary text-black' to="/shops/new"> Cant find your store?</Link></p>
        </div>
    )
}

export default Dashboard
