import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { useParams, useNavigate, Link } from 'react-router-dom'


// Get the details to pre-populate
//1. get id from params (useParams())
// 2. use the id to get info from api : axios
// 3. display info on load: useEffect
// Creating the form
// 4 form input : onchange , useState
// 5. form submit : handelSubmit
// 6. send formData into api ; axios
// 7. logic after update ; redirect to detail page 

const Edit = () => {
    //from create page 
    const [store, setStore] = useState('')
    const [number, setNumber] = useState(70000)
    const [isOpen, setIsOpen] = useState(false)
    const navigate = useNavigate()

    const [errorList, setErrorList] = useState([])

    //from details 
    const { _id } = useParams()

    useEffect(() => {
        axios.get(`http://localhost:8000/api/shops/${_id}`)
            .then(res => {
                const shop = res.data
                console.log(res)
                setStore(shop.store)
                setNumber(shop.number)
                setIsOpen(shop.isOpen)
            })
            .catch(error => console.log(error))
    }, [_id])


    const handleSubmit = (event) => {
        event.preventDefault()
        axios.put(`http://localhost:8000/api/shops/${_id}/update`, { store, number, isOpen })
            .then(res => navigate(`/shops/${_id}`))
            .catch(error => {
                const errorRespondData = error.response.data.errors
                const tempErrorArray = []
                for (const eachKey in errorRespondData) {
                    tempErrorArray.push(errorRespondData[eachKey].message)
                }
                setErrorList(tempErrorArray)
            })
    }


    const handleDelete = () => {
        axios.delete(`http://localhost:8000/api/shops/${_id}`)
            .then(res => navigate(`/`))
            .catch(error => console.log(error))
    }

    return (
        <div>
            <form className='form' onSubmit={handleSubmit}>
                <div>
                    <label>Store: </label>
                    <input type="text" name="store" value={store} className='form-control'
                        onChange={event => setStore(event.target.value)} />
                </div>
                <div>
                    <label>Store Number: </label>
                    <input type="number" name="number" value={number} className='form-control'
                        onChange={event => setNumber(event.target.value)} />
                </div>
                <div>
                    <label>Is Open: </label>
                    <input type="checkbox" name="isOpen" checked={isOpen}
                        onChange={event => setIsOpen(event.target.checked)} />
                </div>
                <button className='btn btn-outline-success me-2 text-black' type="submit">Submit</button>
                <Link className='btn btn-outline-warning text-black' to='/'>Cancel</Link>
                <button type="button" className='btn btn-outline-danger'
                    onClick={handleDelete}>Delete</button>
                {
                    errorList.map((eachError, idx) => (
                        <p className='text-danger' key={idx}>{eachError}</p>
                    ))
                }
            </form>
        </div>
    )
}

export default Edit
